import React from 'react';

const Counter = () => {
    let rr = 0;

    return (
        <>
            <h1  style={{color: "black", textAlign: 'center'}}>Tally counter</h1>
            <button onClick={() => rr(rr+1)}>+</button>
            <button onClick={() => rr(rr-1)}>-</button>
            <button onClick={() => rr(rr*2)}>x2</button>
        </>
    )
    
}

export default Counter;
