import React, {useState} from 'react';

const Register = (props) => {
    const [nuser, setUser] = useState("");
    const [npassword, setPassword] = useState("");
    const [dpassword,setdPassword] = useState("");

    const onRegistro = (e) => {
        console.log(e.target.name)
        if (e.target.name === "user"){
            setUser(e.target.value)
        }
        else if (e.target.name === "password"){
            setPassword(e.target.value)
        }
        else if (e.target.name === "dpassword"){
            setdPassword(e.target.value)
        }
    }

    const onRevisar = (e) => {
        console.log(e)
        e.preventDefault();
        if (user === {user} && password === dpassword === {password}){
            alert("Registro exitoso")
            props.cambioR(true)
        }
        else {
            alert("Las contraseñas no coinciden")
        }
    }

    <h1 style={{color: "black", textAlign: 'center'}}>Registro de usuario</h1>
    
    return(
        <form action="" onRevisar={onRevisar}>

            <div>
                <label htmlFor='user'>Usuario</label>
                <input 
                    type="text" 
                    name="user" 
                    id="user"
                    value={user}
                    onRegistro={onRegistro}
                />
            </div>
            <div>
                <label htmlFor='password'>Contraseña</label>
                <input 
                    type="password" 
                    name="password" 
                    id="password"
                    value={password}
                    onRegistro={onRegistro}
                />
            </div>
            <div>
                <label htmlFor='password'>Confirme Contraseña</label>
                <input 
                    type="password" 
                    name="dpassword" 
                    id="dpassword"
                    value={dpassword}
                    onRegistro={onRegistro}
                />
            </div>
            <button>Iniciar sesión</button>
        </form>
    );
}

export default Register;
