import React, {useState} from 'react';
import ReactDOM from 'react-dom/client';
//Importar componentes
import Alumno from './components/Alumno';

const root = ReactDOM.createRoot(document.getElementById('root'));

const Jsx = () => {
  const [sesion, ChangeSs] = useState(true);
  /*let sesion = true;
    const ChangeSs = () => {
    console.log(sesion)
    sesion = false;
    console.log(sesion)*/
  
  return (
    <>
      {sesion === true ? (
        <>
          <Alumno/>
          <button onClick={() => ChangeSs(false)}>Cerrar sesión</button>
        </>
      ):(
        <>
          <h2>No has iniciado sesión</h2>
          <button onClick={() => ChangeSs(true)}>Iniciar sesión</button>
        </>
      )}
    </>

  );
};

root.render(<Jsx/>);
